$(document).ready(function() {
    frappe.call({
    method: 'jute_mark_india.jute_mark_india.page.dashboard_jmi_applic.dashboard_jmi_applic.fetch_states_data',
    args: {},
    callback: function(response) {
        if (response.message) {
            var states_data = response.message;
            const table = document.getElementById("table5");
            const tableBody = table.getElementsByTagName("tbody")[0];
            for (let i = 0; i < tableBody.rows.length; i++) {
            	const row = tableBody.rows[i];
            	const stateNameCell = row.cells[0];
            	const stateName = stateNameCell.textContent.trim();
            	const stateData = states_data.find(data => data.state === stateName);
            	if (stateData) {
            		row.cells[1].textContent = stateData.application_received;
            		row.cells[2].textContent = stateData.registered_application;
            		row.cells[3].textContent = stateData.sold_indent;
            		row.cells[4].textContent = stateData.sold_labels;
            	}
            	//if(!stateData && state_data)
            }
        }
    }
});
})

var userinfo =$('#user-info')
var errorinfo =$('#resp-error-info')
errorinfo.hide()
userinfo.hide();

$('#search-button').on('click', function() {
    var regNo = $('#reg_no').val();
    if (regNo) {
      frappe.call({
        method: 'jute_mark_india.jute_mark_india.page.dashboard_jmi_applic.dashboard_jmi_applic.get_registration_info',
        args: {
          reg_no: regNo,
        },
        callback: function(response) {
          if (response.message) {
            var data = response.message;
            if (data.registration_number ===null){
                $('#entity_full_name').text('');
                $('#reg_no_info').text('');
                $('#next_renewal_date').text('');
                $('#district').text('');
                $('#state').text('');
                userinfo.hide();
                errorinfo.show()
            }else{
                $('#entity_full_name').text(data.entity_full_name || data.applicant_name);
                $('#reg_no_info').text(data.registration_number);
                $('#next_renewal_date').text(data.next_renewal_date);
                $('#district').text(data.district);
                $('#state').text(data.state);
                errorinfo.hide()
                userinfo.show()
            }
          }else{
            $('#entity_full_name').text('');
            $('#reg_no_info').text('');
            $('#next_renewal_date').text('');
            $('#district').text('');
            $('#state').text('');
            userinfo.hide();
            errorinfo.show()
          }
        }
      });
    }else{
        $('#entity_full_name').text('');
        $('#reg_no_info').text('');
        $('#next_renewal_date').text('');
        $('#district').text('');
        $('#state').text('');
        errorinfo.hide()
        userinfo.hide();
    }
  });
